function hasClass(a,b){return a.className&&new RegExp("(^|\\s)"+b+"(\\s|$)").test(a.className)}function activeState(a,b){var c=document.getElementById(a),b=document.getElementById(b);c.onclick=function(){hasClass(c,"active")?(c.setAttribute("class",""),b.setAttribute("class","")):(c.setAttribute("class","active"),b.setAttribute("class","active"))}}